#include <node.h>\n#include <v8.h>\n\nnamespace demo {\n\nusing v8::FunctionCallbackInfo;\nusing v8::Isolate;\nusing v8::Local;\nusing v8::Object;\nusing v8::String;\nusing v8::Value;\n\nvoid Method(const FunctionCallbackInfo<Value>& args) {\n  Isolate* isolate = args.GetIsolate();\n  args.GetReturnValue().Set(String::NewFromUtf8(isolate, "Hello from C++!").ToLocalChecked());\n}\n\nvoid Initialize(Local<Object> exports) {\n  exports->Set(String::NewFromUtf8(Isolate::GetCurrent(), "hello").ToLocalChecked(),\n               v8::FunctionTemplate::New(Isolate::GetCurrent(), Method)->GetFunction());\n}\n\nNODE_MODULE(NODE_GYP_MODULE_NAME, Initialize)\n} // namespace demo 